import React, { useState } from 'react';

const TodoCard = ({ todo, toggleTodo, deleteTodo, index }) => {
  const [dueDate, setDueDate] = useState(todo.dueDate);
  const [skill, setSkill] = useState(todo.skill);

  const updateTodo = (field, value) => {
    if (field === 'dueDate') {
      setDueDate(value);
    } else if (field === 'skill') {
      setSkill(value);
    }
    todo[field] = value;
  };

  return (
    <div className="todo-card">
      <div
        className="task-text"
        style={{ textDecoration: todo.completed ? 'line-through' : '' }}
      >
        {todo.text}
      </div>
      <div>{todo.description}</div>
      <input
        placeholder="Due Date"
        value={dueDate}
        onChange={(e) => updateTodo('dueDate', e.target.value)}
      />
      <input
        placeholder="Skill"
        value={skill}
        onChange={(e) => updateTodo('skill', e.target.value)}
      />
      <div style={{ display: 'inline-block', float: 'right' }}>
        <button onClick={() => toggleTodo(index)}>Toggle</button>
        <button onClick={() => deleteTodo(index)}>Delete</button>
      </div>
    </div>
  );
};

const TodoList = () => {
  const [todos, setTodos] = useState([]);
  const [input, setInput] = useState('');
  const [description, setDescription] = useState('');

  const addTodo = (event) => {
    event.preventDefault();
    setTodos([
      ...todos,
      {
        text: input,
        description: description,
        dueDate: '',
        skill: '',
        completed: false,
      },
    ]);
    setInput('');
    setDescription('');
  };

  const toggleTodo = (index) => {
    const newTodos = [...todos];
    newTodos[index].completed = !newTodos[index].completed;
    setTodos(newTodos);
  };

  const deleteTodo = (index) => {
    const newTodos = todos.filter((_, i) => i !== index);
    setTodos(newTodos);
  };

  return (
    <div>
      <h2>Todo List</h2>
      <form onSubmit={addTodo}>
        <input
          placeholder="Task"
          value={input}
          onChange={(e) => setInput(e.target.value)}
        />
        <input
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
        <button type="submit">Add</button>
      </form>
      <div>
        {todos.map((todo, index) => (
          <TodoCard
            key={index}
            index={index}
            todo={todo}
            toggleTodo={toggleTodo}
            deleteTodo={deleteTodo}
          />
        ))}
      </div>
    </div>
  );
};

export default TodoList;
