import React from 'react';

const TaskModal = ({ selectedTaskIndex, todos, setSelectedTaskIndex /* other necessary props */ }) => {
    const handleCloseModal = (event) => {
      // Prevent propagation of click event to child elements
      event.stopPropagation();
      setSelectedTaskIndex(-1);
    };

    return (
      selectedTaskIndex !== -1 && (
        <div
          style={{
            position: 'fixed',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            backgroundColor: 'rgba(0, 0, 0, 0.5)',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
          }}
          onClick={handleCloseModal}
        >
          <div
            style={{
              backgroundColor: 'white',
              padding: '20px',
              borderRadius: '10px',
              maxWidth: '400px',
              width: '90%',
            }}
            onClick={(event) => event.stopPropagation()} // Prevent click event from propagating to the overlay div
          >
            {/* Render the task details here */}
          </div>
        </div>
      )
    );
  };
  export default TaskModal;
