import React, { useState, useEffect } from 'react';
import { RadarChart, PolarGrid, PolarAngleAxis, Radar } from 'recharts';

const WindRose = ({ data, defaultSkills }) => {
  const [skillsData, setSkillsData] = useState([]);

  const calculateWindRoseSize = (skills) => {
    return skills.length * 50;
  };

  useEffect(() => {
    const updateSkillsData = () => {
      const skillMap = {};

      data.forEach((todo) => {
        if (!skillMap[todo.skill]) {
          skillMap[todo.skill] = {
            skill: todo.skill,
            value: 0,
            taskCount: 0,
          };
        }
        skillMap[todo.skill].taskCount++;

        if (todo.completed) {
          skillMap[todo.skill].value += 100;
        }
      });

      const skills = Object.values(skillMap).map((skillData) => ({
        ...skillData,
        value: skillData.value / skillData.taskCount,
      }));

      setSkillsData(skills);
    };

    updateSkillsData();
  }, [data]);

  const size = calculateWindRoseSize(skillsData.length === 0 ? defaultSkills : skillsData);

  return (
    <div>
      <h2>Wind Rose</h2>
      <RadarChart
        cx={size / 2}
        cy={size / 2}
        outerRadius={size / 4}
        width={size}
        height={size}
        data={skillsData.length === 0 ? defaultSkills : skillsData}
      >
        <PolarGrid />
        <PolarAngleAxis dataKey="skill" />
        <Radar
          name="Skills"
          dataKey="value"
          stroke="#8884d8"
          fill="#8884d8"
          fillOpacity={0.6}
        />
      </RadarChart>
    </div>
  );
};

export default WindRose;
