import React from 'react';

const TaskModal = ({
  selectedTaskIndex,
  todos,
  setSelectedTaskIndex,
  /* other necessary props */
}) => {
  const handleCloseModal = (event) => {
    // Prevent propagation of click event to child elements
    event.stopPropagation();
    setSelectedTaskIndex(-1);
  };

  const selectedTask = todos[selectedTaskIndex];

  return (
    selectedTaskIndex !== -1 && (
      <div
        style={{
          position: 'fixed',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          backgroundColor: 'rgba(0, 0, 0, 0.5)',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
        }}
        onClick={handleCloseModal}
      >
        <div
          style={{
            backgroundColor: 'white',
            padding: '20px',
            borderRadius: '10px',
            maxWidth: '400px',
            width: '90%',
          }}
          onClick={(event) => event.stopPropagation()} // Prevent click event from propagating to the overlay div
        >
          {/* Render the task details here */}
          <h3>{selectedTask.text}</h3>
          <p>Description: {selectedTask.description}</p>
          <p>Due Date: {selectedTask.dueDate}</p>
          <p>Skill: {selectedTask.skill}</p>
          <p>Priority: P{selectedTask.priority}</p>
          <p>Project: {selectedTask.project}</p>
          <p>Label: {selectedTask.label}</p>
          <p>Completed: {selectedTask.completed ? 'Yes' : 'No'}</p>
          <h4>Subtasks:</h4>
          <ul>
            {selectedTask.subtasks.map((subtask, index) => (
              <li key={index}>
                {subtask.text} - {subtask.description}
              </li>
            ))}
          </ul>
        </div>
      </div>
    )
  );
};
export default TaskModal;
