import React, { useState, useEffect } from 'react';
import { RadarChart, PolarGrid, PolarAngleAxis, Radar } from 'recharts';

const WindRose = ({ data, defaultSkills }) => {
  const [skillsData, setSkillsData] = useState([]);

  const calculateWindRoseSize = (skills) => {
    return skills.length * 50;
  };

  useEffect(() => {
    const updateSkillsData = () => {
      // Update the skillsData state based on the data prop (todos)
      // You can modify this function according to your needs
      const skills = data.map((todo) => ({
        skill: todo.skill,
        value: todo.completed ? 100 : 0
      }));
      setSkillsData(skills);
    };

    updateSkillsData();
  }, [data]);

  const size = calculateWindRoseSize(skillsData.length === 0 ? defaultSkills : skillsData);

  return (
    <div>
      <h2>Wind Rose</h2>
      <RadarChart
        cx={size / 2}
        cy={size / 2}
        outerRadius={size / 4}
        width={size}
        height={size}
        data={skillsData.length === 0 ? defaultSkills : skillsData}
      >
        <PolarGrid />
        <PolarAngleAxis dataKey="skill" />
        <Radar
          name="Skills"
          dataKey="value"
          stroke="#8884d8"
          fill="#8884d8"
          fillOpacity={0.6}
        />
      </RadarChart>
    </div>
  );
};

export default WindRose;
